function show_google_viewer(url){



var s = document.getElementById("statement");

if (s) { s.className = "fail"; }



var pdfWindow = document.getElementById("pdf");

if (pdfWindow && pdfWindow.style) { pdfWindow.style.display = 'none'; }



var googlepdfFrame = document.getElementById("googlePDFiframe");

if (googlepdfFrame) { googlepdfFrame.src = url; }



var googlepdfWindow = document.getElementById("googlePDF");

if (googlepdfWindow && googlepdfWindow.style) { googlepdfWindow.style.display = 'block'; }

}



function show_embedded_viewer(url){



var params = {pdfOpenParams: {navpanes: 0,toolbar: 0,statusbar: 0,view: "FitH"}};



try {

PDFObject.embed(url, "#pdf", params);

} catch (e){

alert("PDFObject EMBED ERROR" + e.message);

}



var s = document.getElementById("statement");

if (s) { s.className = "success"; }



var pdfWindow = document.getElementById("pdf");

if (pdfWindow && pdfWindow.style) { pdfWindow.style.display = 'block'; }



var googlepdfFrame = document.getElementById("googlePDFiframe");

if (googlepdfFrame) { googlepdfFrame.src = ''; }



var googlepdfWindow = document.getElementById("googlePDF");

if (googlepdfWindow && googlepdfWindow.style) { googlepdfWindow.style.display = 'none'; }

}



function get_cookie(name){

var a_all_cookies = document.cookie.split(';');

var a_temp_cookie = '';

var cookie_name = '';

var cookie_value = '';

var b_cookie_found = false;

for (i=0;i<a_all_cookies.length;i++){

a_temp_cookie = a_all_cookies[i].split('=');

cookie_name = a_temp_cookie[0].replace(/^\s+|\s+$/g,'');

if ( cookie_name == name ){

b_cookie_found = true;

if (a_temp_cookie.length>1){

cookie_value = unescape(a_temp_cookie[1].replace(/^\s+|\s+$/g,''));

}

return cookie_value;

break;

}

a_temp_cookie = null;

cookie_name = '';

}

if (!b_cookie_found){

return null;

}

}



function set_cookie(name, value){

var today = new Date();

var expires = 365 * 1000 * 60 * 60 * 24;

today.setTime( today.getTime() );

var expires_date = new Date( today.getTime() + (expires) );

document.cookie = name + "=" + escape( value ) +

";expires=" + expires_date.toGMTString() ;

}





var whitespace = " \t\n\r";



function isEmpty(s)

{   return ((s == null) || (s.length == 0))

}



function isWhitespace (s)

{   var i;

if (isEmpty(s)) return true;

for (i = 0; i < s.length; i++)

{

var c = s.charAt(i);

if (whitespace.indexOf(c) == -1) return false;

}

return true;

}













