// Related Post - see more, see less slider

function seeMoreExpand(e) {

if ($(e).children('.moreLessLink').text()=="See more")

{

$(e + ' .dn').addClass('db');

$(e + ' .dn').slideDown(800);

$(e + ' .dn').removeClass('dn');

$(e).children('.moreLessLink').html("See less<i class='fas fa-angle-up ml1'></i>");

$('html,body').animate({scrollTop: $(e).offset().top},'slow');

}

else

{

$(e + ' .db').slideUp(800);

$(e + ' .db').addClass('dn');

$(e).children('.moreLessLink').html("See more<i class='fas fa-angle-down ml1'></i>");

$('html,body').animate({scrollTop: $(e).offset().top},'slow');

$(e + ' .db').removeClass('db');

$(e + ' .dn').attr('style','display: none');

}

}














